let interval_slot=null,timer_slot=null;window.addEventListener("DOMContentLoaded",()=>{const t=document.getElementById("header-banner");if(t===null)return;sessionStorage.getItem("hide-banner")==="true"&&closeAd();const e=t.getElementsByClassName("br-flair")[0],n=e.children.length,s=counterClosure((t,n)=>{const s=e.children[t],o=e.children[(t+1)%n];s.classList.contains("in")&&(s.classList.remove("in"),s.classList.add("out")),o.classList.remove("out"),o.classList.add("in")},n,n);timer_slot=setTimeout(()=>{interval_slot=setInterval(()=>{s()},4e3),s()},2e3)});function closeAd(){timer_slot&&(clearTimeout(timer_slot),timer_slot=null),interval_slot&&(clearInterval(interval_slot),interval_slot=null);const e=document.getElementById("header-banner");if(e===null)return;e.parentElement.removeChild(e),sessionStorage.setItem("hide-banner","true")}function showMenu(){const e=document.getElementById("h_menu");e.classList.remove("hide"),e.classList.add("show")}function hideMenu(){const e=document.getElementById("h_menu");e.classList.remove("show"),e.classList.add("hide")}